import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Importa useParams
import { updateUser, getUsers } from '../services/UserService';

const EditUser = () => {
  const [name, setName] = useState('');
  const { id } = useParams(); // Usa useParams para obtener los parámetros de la URL

  useEffect(() => {
    getUsers().then(response => {
      const user = response.data.find(user => user.id === parseInt(id));
      setName(user ? user.name : ''); // Asegúrate de manejar el caso donde el usuario no exista
    });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    updateUser(id, { name }).then(response => console.log(response.data));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" />
      <button type="submit">Update User</button>
    </form>
  );
};

export default EditUser;
